import {
    Chart,
    ChartCategoryAxis,
    ChartCategoryAxisItem,
    ChartSeries,
    ChartSeriesItem
} from '@progress/kendo-react-charts';
import 'hammerjs';

import  React, { useState, useEffect, useLayoutEffect } from  "react";
import { waterUsageStore, sendMessages} from '../store/water-usage'


const ChartContainer = () => {
    const [waterState, setWaterState] = useState(waterUsageStore.initialState);

    useEffect(()=> {
        waterUsageStore.subscribe(setWaterState);
        waterUsageStore.init();
        sendMessages(); // start simulated messages from backend
    },[]);

    return (
        <Chart style={{width: '600px', height: '500px'}}>
            <ChartSeries>
                <ChartSeriesItem data={waterState.data} type="column" field="volume" categoryField="month" />
            </ChartSeries>
        </Chart>
    )
};

// Use for <ChartContainer seriesData={seriesData2} categories={categories} />
const ChartContainer2 = ({seriesData, categories}) => (
    <Chart style={{width: '700px', height: '500px'}}>
        <ChartCategoryAxis>
            <ChartCategoryAxisItem categories={categories} />
        </ChartCategoryAxis>
        <ChartSeries>
            <ChartSeriesItem data={seriesData} />
        </ChartSeries>
    </Chart>
);

export default ChartContainer;
